  --foreground: 20 14.3% 4.1%;
  --muted: 60 4.8% 95.9%;
  --muted-foreground: 25 5.3% 44.7%;
  --popover: 0 0% 100%;
  --popover-foreground: 20 14.3% 4.1%;
  --card: 0 0% 100%;
  --card-foreground: 20 14.3% 4.1%;
  --border: 20 5.9% 90%;
  --input: 20 5.9% 90%;
  --primary: 207 90% 54%;
  --primary-foreground: 211 100% 99%;
  --secondary: 60 4.8% 95.9%;
  --secondary-foreground: 24 9.8% 10%;
  --accent: 60 4.8% 95.9%;
  --accent-foreground: 24 9.8% 10%;
  --destructive: 0 84.2% 60.2%;
  --destructive-foreground: 60 9.1% 97.8%;
  --ring: 20 14.3% 4.1%;
  --radius: 0.5rem;
  --header-height: 80px; /* Mobile-first: smaller header */
  --sidebar-width: 280px;
  --touch-target: 44px; /* Minimum touch target size */

  /* Blueshift-inspired Ultra Dark Theme */
  --blueshift-black: 0 0% 0%; /* Pure black #000000 */
  --blueshift-dark-950: 240 5% 2%; /* Near black #050507 */
  --blueshift-dark-900: 240 6% 4%; /* Ultra dark #0a0a0f */
  --blueshift-dark-800: 240 7% 6%; /* Very dark #0f0f18 */
  --blueshift-dark-700: 240 8% 10%; /* Dark gray #181820 */
  --blueshift-dark-600: 240 9% 15%; /* Medium dark #222230 */
  --blueshift-dark-500: 240 10% 20%; /* Lighter dark #2d2d40 */

  /* Vibrant Neon Cyan System (Blueshift signature) */
  --blueshift-neon: 180 100% 50%; /* Pure cyan #00FFFF */
  --blueshift-electric: 180 100% 47%; /* Electric cyan #00EAFF */
  --blueshift-cyan-50: 180 100% 97%; /* Very light cyan */
  --blueshift-cyan-100: 180 100% 90%; /* Light cyan */
  --blueshift-cyan-200: 180 100% 80%; /* Medium light cyan */
  --blueshift-cyan-300: 180 100% 70%; /* Medium cyan */
  --blueshift-cyan-400: 180 100% 60%; /* Bright cyan */
  --blueshift-cyan-500: 180 100% 50%; /* Pure cyan */
  --blueshift-cyan-600: 180 100% 40%; /* Dark cyan */
  --blueshift-cyan-700: 180 100% 30%; /* Darker cyan */
  --blueshift-cyan-800: 180 100% 20%; /* Very dark cyan */
  --blueshift-cyan-900: 180 100% 10%; /* Ultra dark cyan */

  /* Secondary Purple Accents */
  --blueshift-purple-400: 268 100% 74%; /* #a78bfa */
  --blueshift-purple-500: 268 56% 59%; /* #8b5cf6 */
  --blueshift-purple-600: 267 57% 51%; /* #7c3aed */
  --blueshift-purple-700: 267 66% 44%; /* #6d28d9 */
  --blueshift-purple-800: 267 83% 35%; /* #5b21b6 */
  --blueshift-purple-900: 267 83% 29%; /* #4c1d95 */

  /* Legacy compatibility */
  --tech-cyan-50: var(--blueshift-cyan-50);
  --tech-cyan-100: var(--blueshift-cyan-100);
  --tech-cyan-200: var(--blueshift-cyan-200);
  --tech-cyan-300: var(--blueshift-cyan-300);
  --tech-cyan-400: var(--blueshift-cyan-400);
  --tech-cyan-500: var(--blueshift-cyan-500);
  --tech-cyan-600: var(--blueshift-cyan-600);
  --tech-cyan-700: var(--blueshift-cyan-700);
  --tech-cyan-800: var(--blueshift-cyan-800);
  --tech-cyan-900: var(--blueshift-cyan-900);

  --tech-purple-400: var(--blueshift-purple-400);
  --tech-purple-500: var(--blueshift-purple-500);
  --tech-purple-600: var(--blueshift-purple-600);
  --tech-purple-700: var(--blueshift-purple-700);
