.dark {
  /* Blueshift Ultra Dark Theme */
  --background: var(--blueshift-black);
  --foreground: 0 0% 98%;
  --muted: var(--blueshift-dark-700);
  --muted-foreground: 240 5% 64.9%;
  --popover: var(--blueshift-dark-900);
  --popover-foreground: 0 0% 98%;
  --card: var(--blueshift-dark-900);
  --card-foreground: 0 0% 98%;
  --border: var(--blueshift-dark-600);
  --input: var(--blueshift-dark-700);
  --primary: var(--blueshift-neon);
  --primary-foreground: var(--blueshift-black);
  --secondary: var(--blueshift-dark-700);
  --secondary-foreground: 0 0% 98%;
  --accent: var(--blueshift-neon);
  --accent-foreground: var(--blueshift-black);
  --destructive: 0 62.8% 30.6%;
