}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply antialiased bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  }
}

/* Blueshift-inspired Custom Styles */
.data-particle {
  position: fixed;
  width: 2px;
  height: 2px;
  background: rgba(0, 255, 255, 0.8);
  border-radius: 2px;
  pointer-events: none;
  animation: data-flow 15s infinite linear;
  box-shadow: 0 0 6px rgba(0, 255, 255, 0.6);
}

.data-particle:nth-child(odd) {
  animation-delay: -5s;
  animation-duration: 20s;
  background: rgba(0, 255, 255, 0.6);
  box-shadow: 0 0 4px rgba(0, 255, 255, 0.4);
}

.data-particle:nth-child(3n) {
  animation-delay: -10s;
  animation-duration: 25s;
  background: rgba(147, 51, 234, 0.6);
  box-shadow: 0 0 4px rgba(147, 51, 234, 0.4);
}

.btn-tech-primary {
  @apply bg-cyan-500 hover:bg-cyan-400 text-black font-bold px-6 py-3 rounded-lg shadow-lg transform transition-all duration-200 hover:scale-105 hover:shadow-xl border-2 border-cyan-400 font-mono;
}

.btn-tech-secondary {
  @apply bg-gray-800 hover:bg-gray-700 text-cyan-400 font-bold px-6 py-3 rounded-lg shadow-lg transform transition-all duration-200 hover:scale-105 hover:shadow-xl border-2 border-gray-600 font-mono;
}

.btn-tech-accent {
  @apply bg-purple-600 hover:bg-purple-500 text-white font-bold px-6 py-3 rounded-lg shadow-lg transform transition-all duration-200 hover:scale-105 hover:shadow-xl border-2 border-purple-400 font-mono;
}

.tech-card {
  @apply bg-gray-900/50 border-2 border-cyan-400/30 rounded-lg shadow-xl backdrop-blur-sm;
}

.code-editor {
  background: linear-gradient(145deg, #1e1e1e, #2d2d2d);
  border: 2px solid #444;
  font-family: 'Fira Code', monospace;
}

.progress-trail {
  background: linear-gradient(90deg, transparent 0%, rgba(212, 165, 116, 0.3) 50%, transparent 100%);
}

@keyframes dataStreamFlow {
  0% {
    transform: translateY(0) translateX(0) scale(0.5);
    opacity: 0;
  }
  20% {
    opacity: 1;
    transform: translateY(10vh) translateX(5px) scale(1);
  }
  80% {
    opacity: 1;
    transform: translateY(80vh) translateX(-5px) scale(1);
  }
  100% {
    transform: translateY(100vh) translateX(0) scale(0.5);
    opacity: 0;
  }
}

.sparkle-effect {
  position: absolute;
  width: 4px;
  height: 4px;
  background: radial-gradient(circle, #ffd700, transparent);
  border-radius: 50%;
  animation: sparkle 1.5s ease-in-out infinite;
}

@keyframes sparkle {
  0%, 100% { opacity: 0; transform: scale(0); }
  50% { opacity: 1; transform: scale(1); }
}

@keyframes data-flow {
  0% { transform: translateX(-100px) translateY(0) rotate(0deg); opacity: 0; }
  10% { opacity: 0.8; }
  90% { opacity: 0.8; }
  100% { transform: translateX(100vw) translateY(-20px) rotate(90deg); opacity: 0; }
}

@keyframes tech-pulse {
  0%, 100% { transform: scale(1); filter: brightness(1) hue-rotate(0deg); }
  50% { transform: scale(1.02); filter: brightness(1.1) hue-rotate(10deg); }
}

/* Blueshift-specific animations */
@keyframes blueshift-glow {
  0%, 100% { 
    box-shadow: 0 0 5px rgba(0, 255, 255, 0.2), 0 0 10px rgba(0, 255, 255, 0.1), inset 0 0 10px rgba(0, 255, 255, 0.05);
  }
  50% { 
    box-shadow: 0 0 10px rgba(0, 255, 255, 0.4), 0 0 20px rgba(0, 255, 255, 0.2), inset 0 0 15px rgba(0, 255, 255, 0.1);
  }
}

@keyframes blueshift-scan {
  0% { background-position: -100% 0; }
  100% { background-position: 100% 0; }
}

/* Enhanced Timeline Animations */
@keyframes timeline-fade-in {
  from { 
    opacity: 0; 
    transform: translateY(20px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes timeline-reveal {
  from {
    opacity: 0;
    transform: scale(0.95) translateY(10px);
  }
  to {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}

.timeline-fade-in {
  animation: timeline-fade-in 0.8s ease-out;
}

.timeline-reveal {
  animation: timeline-reveal 0.6s ease-out;
}

/* Mobile responsiveness for timeline */
@media (max-width: 640px) {
  .timeline-mobile-spacing > * + * {
    margin-top: 2rem !important;
  }

  .timeline-mobile-cards {
    padding-left: 3rem;
  }
}

/* Touch-friendly controls */
@media (max-width: 768px) {
  .timeline-controls button {
    min-height: 44px;
    min-width: 44px;
  }
}

/* Ethos Page Enhanced Styles */
@keyframes ethos-section-reveal {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.ethos-section-reveal {
  animation: ethos-section-reveal 0.6s ease-out;
}

/* Custom scrollbar for navigation */
.ethos-nav-scroll::-webkit-scrollbar {
  height: 2px;
}

.ethos-nav-scroll::-webkit-scrollbar-track {
  background: rgba(55, 65, 81, 0.1);
}

.ethos-nav-scroll::-webkit-scrollbar-thumb {
  background: rgba(34, 211, 238, 0.3);
  border-radius: 1px;
}

/* Video overlay hover effects */
.ethos-video-overlay:hover .ethos-play-button {
  transform: scale(1.1);
}

/* Enhanced mobile responsiveness for ethos page */
@media (max-width: 640px) {
  .ethos-container {
    padding-left: 1rem;
    padding-right: 1rem;
  }

  .ethos-nav-mobile {
    flex-wrap: nowrap;
    overflow-x: auto;
    scrollbar-width: thin;
  }
}

/* Accordion animations for mobile */
@keyframes accordion-slide-down {
  from {
    max-height: 0;
    opacity: 0;
  }
  to {
    max-height: 1000px;
    opacity: 1;
  }
}

@keyframes accordion-slide-up {
  from {
    max-height: 1000px;
    opacity: 1;
  }
  to {
    max-height: 0;
    opacity: 0;
  }
}

.accordion-enter {
  animation: accordion-slide-down 0.3s ease-in-out;
}

.accordion-exit {
  animation: accordion-slide-up 0.3s ease-in-out;
}

/* Mobile tab navigation enhancements */
@media (max-width: 768px) {
  .ethos-nav-scroll {
    scrollbar-width: none;
    -ms-overflow-style: none;
  }

  .ethos-nav-scroll::-webkit-scrollbar {
    display: none;
  }

  /* Touch-friendly tab buttons */
  .ethos-nav-scroll button {
    touch-action: manipulation;
    -webkit-tap-highlight-color: transparent;
  }

  /* Ensure sufficient touch targets */
  .ethos-mobile-tab {
    min-height: 44px;
    min-width: 44px;
  }
}

/* Enhanced accordion styling */
.accordion-header {
  transition: all 0.2s ease-in-out;
}

.accordion-header:active {
  transform: scale(0.98);
}

.accordion-content {
  transition: max-height 0.3s ease-in-out, opacity 0.3s ease-in-out;
}

@keyframes glow-tech {
  0% { box-shadow: 0 0 10px rgba(0, 255, 255, 0.4), 0 0 20px rgba(0, 255, 255, 0.2); }
  50% { box-shadow: 0 0 20px rgba(0, 255, 255, 0.6), 0 0 40px rgba(0, 255, 255, 0.3); }
  100% { box-shadow: 0 0 15px rgba(147, 51, 234, 0.5), 0 0 30px rgba(147, 51, 234, 0.3); }
}

@keyframes fade-in {
  0% { opacity: 0; transform: translateY(10px); }
  100% { opacity: 1; transform: translateY(0); }
}

@keyframes slide-up {
  0% { opacity: 0; transform: translateY(30px); }
  100% { opacity: 1; transform: translateY(0); }
}

.animate-dust-swirl { animation: dust-swirl 20s infinite linear; }
.animate-sheriff-star { animation: sheriff-star 3s ease-in-out infinite; }
.animate-glow-pulse { animation: glow-pulse 2s ease-in-out infinite alternate; }
.animate-fade-in { animation: fade-in 0.5s ease-out; }
.animate-slide-up { animation: slide-up 0.5s ease-out; }

/* Mobile-First Utilities */
.touch-target {
  min-height: var(--touch-target);
  min-width: var(--touch-target);
}

.mobile-nav-toggle {
  @apply flex items-center justify-center w-11 h-11 rounded-lg bg-tech-purple-800/50 border border-tech-cyan-400/30 touch-target;
}

.mobile-sidebar {
  @apply fixed inset-y-0 left-0 z-50 w-72 bg-gradient-to-b from-tech-purple-900 to-tech-purple-800 border-r border-tech-cyan-400/30 transform transition-transform duration-300 ease-in-out;
}

.mobile-sidebar-open {
  @apply translate-x-0;
}

.mobile-sidebar-closed {
  @apply -translate-x-full;
}

.mobile-overlay {
  @apply fixed inset-0 bg-black/50 z-40 transition-opacity duration-300;
}

/* Typography - Mobile First */
.text-mobile-sm { font-size: 0.875rem; line-height: 1.25rem; } /* 14px */
.text-mobile-base { font-size: 1rem; line-height: 1.5rem; } /* 16px */
.text-mobile-lg { font-size: 1.125rem; line-height: 1.75rem; } /* 18px */
.text-mobile-xl { font-size: 1.25rem; line-height: 1.75rem; } /* 20px */
.text-mobile-2xl { font-size: 1.5rem; line-height: 2rem; } /* 24px */
.text-mobile-3xl { font-size: 1.875rem; line-height: 2.25rem; } /* 30px */

/* Responsive spacing utilities - Mobile First */
.space-mobile-2 { margin: 0.5rem; }
.space-mobile-4 { margin: 1rem; }
.space-mobile-6 { margin: 1.5rem; }
.space-mobile-8 { margin: 2rem; }

.p-mobile-3 { padding: 0.75rem; }
.p-mobile-4 { padding: 1rem; }
.p-mobile-6 { padding: 1.5rem; }

/* Button mobile-first sizes */
.btn-mobile {
  @apply px-4 py-3 text-mobile-base font-medium rounded-lg touch-target flex items-center justify-center;
}

.btn-mobile-sm {
  @apply px-3 py-2 text-mobile-sm font-medium rounded-md;
  min-height: 40px;
  min-width: 40px;
}

/* Card mobile-first */
.card-mobile {
  @apply rounded-xl p-mobile-4 shadow-lg;
}

.card-mobile-compact {
  @apply rounded-lg p-mobile-3 shadow-md;
}

/* Matrix-style text shadow for challenge rewards */
.text-shadow-matrix {
  text-shadow: 
    0 0 5px hsl(var(--tech-cyan)),
    0 0 10px hsl(var(--tech-purple)),
    0 0 15px hsl(var(--tech-cyan)),
    0 0 20px hsl(var(--tech-purple));
}

.text-shadow-matrix-glow {
  text-shadow: 
    0 0 10px hsl(var(--tech-cyan)),
    0 0 20px hsl(var(--tech-cyan)),
    0 0 30px hsl(var(--tech-cyan)),
    0 0 40px hsl(var(--tech-cyan));
  animation: matrix-pulse 2s ease-in-out infinite alternate;
}

@keyframes matrix-pulse {
  from {
    text-shadow: 
      0 0 10px hsl(var(--tech-cyan)),
      0 0 20px hsl(var(--tech-cyan)),
      0 0 30px hsl(var(--tech-cyan)),
      0 0 40px hsl(var(--tech-cyan));
  }
  to {
    text-shadow: 
      0 0 5px hsl(var(--tech-cyan)),
      0 0 10px hsl(var(--tech-cyan)),
      0 0 15px hsl(var(--tech-cyan)),
      0 0 20px hsl(var(--tech-cyan));
  }
}

/* Fade in animation for cypherpunk module */
@keyframes fade-in {
  0% { opacity: 0; transform: translateY(20px); }
  100% { opacity: 1; transform: translateY(0); }
}

.animate-fade-in {
  animation: fade-in 1s ease-out forwards;
}

