import { useEffect } from 'react';
import { useLoadingManager } from './use-loading-manager';

export function usePageLoader() {
  const { showLoader, startLoading, stopLoading } = useLoadingManager({
    threshold: 800, // Show loader if page takes more than 800ms
    minDisplayTime: 1500 // Show for at least 1.5 seconds once displayed
  });

  useEffect(() => {
    // Start loading when component mounts
    startLoading();

    // Simulate page content loading
    const loadTimer = setTimeout(() => {
      stopLoading();
    }, Math.random() * 1000 + 500); // Random load time between 500-1500ms

    return () => {
      clearTimeout(loadTimer);
      stopLoading();
    };
  }, [startLoading, stopLoading]);

  return { showLoader };
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/hooks/use-wallet.ts.content:
import { useState, useEffect, useCallback } from 'react';

interface WalletState {
  connected: boolean;
  address: string | null;
  connecting: boolean;
}

// Phantom wallet interface
interface PhantomWallet {
  isPhantom?: boolean;
  connect: () => Promise<{ publicKey: { toString(): string } }>;
  disconnect: () => Promise<void>;
  isConnected: boolean;
  publicKey: { toString(): string } | null;
  on: (event: string, callback: () => void) => void;
  off: (event: string, callback: () => void) => void;
}

declare global {
  interface Window {
    solana?: PhantomWallet;
    phantom?: {
      solana?: PhantomWallet;
    };
  }
}

export function useWallet() {
  const [state, setState] = useState<WalletState>({
    connected: false,
    address: null,
    connecting: false
  });

  // Get the wallet adapter (prioritize Phantom)
  const getWallet = useCallback((): PhantomWallet | null => {
    if (typeof window === 'undefined') return null;

    // Check for Phantom
    if (window.phantom?.solana?.isPhantom) {
      return window.phantom.solana;
    }

    // Fallback to generic solana object
    if (window.solana?.isPhantom) {
      return window.solana;
    }

    return null;
  }, []);

  // Check for existing connection on load
  useEffect(() => {
    const wallet = getWallet();
    if (wallet && wallet.isConnected && wallet.publicKey) {
      setState({
        connected: true,
        address: wallet.publicKey.toString(),
        connecting: false
      });
    }
  }, [getWallet]);

  const connect = async () => {
    const wallet = getWallet();

    if (!wallet) {
      throw new Error('No Solana wallet found. Please install Phantom wallet.');
    }

    setState(prev => ({ ...prev, connecting: true }));

    try {
      const response = await wallet.connect();
      const address = response.publicKey.toString();

      setState({
        connected: true,
        address,
        connecting: false
      });

      localStorage.setItem('wallet-connected', 'true');
      localStorage.setItem('wallet-address', address);
    } catch (error) {
      setState(prev => ({ ...prev, connecting: false }));
      throw error;
    }
  };

  const disconnect = useCallback(async () => {
    const wallet = getWallet();

    if (wallet && wallet.disconnect) {
      try {
        await wallet.disconnect();
      } catch (error) {
        console.warn('Error disconnecting wallet:', error);
      }
    }

    setState({
      connected: false,
      address: null,
      connecting: false
    });

    localStorage.removeItem('wallet-connected');
    localStorage.removeItem('wallet-address');
  }, [getWallet]);

  // Listen for wallet events
  useEffect(() => {
    const wallet = getWallet();
    if (!wallet) return;

    const handleAccountChanged = () => {
      if (wallet.publicKey) {
        setState(prev => ({
          ...prev,
          address: wallet.publicKey!.toString()
        }));
      } else {
        disconnect();
      }
    };

    wallet.on('accountChanged', handleAccountChanged);
    wallet.on('disconnect', disconnect);

    return () => {
      wallet.off('accountChanged', handleAccountChanged);
      wallet.off('disconnect', disconnect);
    };
  }, [getWallet, disconnect]);

  return {
    ...state,
    connect,
    disconnect,
    walletInstalled: !!getWallet()
  };
}

