import { useState, useEffect, useRef } from "react";
import { TechCard } from "@/components/ui/TechCard";
import { TechButton } from "@/components/ui/TechButton";

interface TimelineEvent {
  year: string;
  title: string;
  description: string;
  impact: string;
  classification: string;
}

const events: TimelineEvent[] = [
  {
    year: "1976",
    title: "Public Key Cryptography Discovered",
    description: "Whitfield Diffie and Martin Hellman publish 'New Directions in Cryptography', introducing the concept of public-key cryptography. This breakthrough allows secure communication without sharing secret keys.",
    impact: "Foundation for all modern secure communications",
    classification: "DECLASSIFIED"
  },
  {
    year: "1991", 
    title: "PGP Released to the World",
    description: "Phil Zimmermann releases Pretty Good Privacy (PGP), making military-grade encryption available to civilians. The US government considers this a violation of arms export restrictions.",
    impact: "Democratized encryption for the masses",
    classification: "RESTRICTED"
  },
  {
    year: "1993",
    title: "The Clipper Chip Controversy",
    description: "The US government proposes the Clipper Chipâ€”an encryption device with a government backdoor. Cypherpunks rally against this attempt to weaken privacy, ultimately defeating the proposal.",
    impact: "Established precedent against government backdoors",
    classification: "TOP SECRET"
  },
  {
    year: "1995",
    title: "SSL Protocol Introduced",
    description: "Netscape introduces Secure Sockets Layer (SSL), bringing cryptographic security to web browsing. This protocol becomes the foundation for HTTPS and secure e-commerce.",
    impact: "Enabled secure web commerce and communications",
    classification: "DECLASSIFIED"
  },
  {
    year: "2001",
    title: "Advanced Encryption Standard (AES)",
    description: "The US government adopts AES as the official encryption standard, replacing DES. This cipher, originally called Rijndael, becomes the worldwide standard for symmetric encryption.",
    impact: "Standardized modern encryption globally",
    classification: "DECLASSIFIED"
  }
];

interface CryptoWarsTimelineProps {
  onComplete?: () => void;
}

export function CryptoWarsTimeline({ onComplete }: CryptoWarsTimelineProps) {
  // State for automated progression
  const [currentEventIndex, setCurrentEventIndex] = useState(-1);
  const [isPlaying, setIsPlaying] = useState(false);
  const [hasStarted, setHasStarted] = useState(false);
  const [visibleEvents, setVisibleEvents] = useState<number[]>([]);
  const [isComplete, setIsComplete] = useState(false);

  // Refs for scroll management
  const timelineRef = useRef<HTMLDivElement>(null);
  const eventRefs = useRef<(HTMLDivElement | null)[]>([]);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  // Auto-progression timing (3 seconds per event)
  const PROGRESSION_DELAY = 3000;

  // Initialize event refs array
  useEffect(() => {
    eventRefs.current = eventRefs.current.slice(0, events.length);
  }, []);

  // Automated progression logic
  useEffect(() => {
    if (isPlaying && hasStarted) {
      intervalRef.current = setInterval(() => {
        setCurrentEventIndex(prev => {
          const nextIndex = prev + 1;
          if (nextIndex >= events.length) {
            setIsPlaying(false);
            setIsComplete(true);
            return prev;
          }
          return nextIndex;
        });
      }, PROGRESSION_DELAY);
    } else {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    }

    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isPlaying, hasStarted]);

  // Update visible events and scroll when current event changes
  useEffect(() => {
    if (currentEventIndex >= 0) {
      setVisibleEvents(prev => {
        if (!prev.includes(currentEventIndex)) {
          return [...prev, currentEventIndex];
        }
        return prev;
      });

      // Scroll to current event (with delay for animation)
      setTimeout(() => {
        const eventElement = eventRefs.current[currentEventIndex];
        if (eventElement && timelineRef.current) {
          eventElement.scrollIntoView({
            behavior: 'smooth',
            block: 'center',
            inline: 'nearest'
          });
        }
      }, 200);
    }
  }, [currentEventIndex]);

  // Control functions
  const startProgression = () => {
    setHasStarted(true);
    setIsPlaying(true);
    if (currentEventIndex === -1) {
      setCurrentEventIndex(0);
    }
  };

  const pauseProgression = () => {
    setIsPlaying(false);
  };

  const goToNextEvent = () => {
    if (currentEventIndex < events.length - 1) {
      setCurrentEventIndex(prev => prev + 1);
    } else if (currentEventIndex === events.length - 1) {
      setIsComplete(true);
    }
  };

  const goToPreviousEvent = () => {
    if (currentEventIndex > 0) {
      setCurrentEventIndex(prev => prev - 1);
    }
  };

  const resetTimeline = () => {
    setCurrentEventIndex(-1);
    setVisibleEvents([]);
    setIsPlaying(false);
    setHasStarted(false);
    setIsComplete(false);
    if (timelineRef.current) {
      timelineRef.current.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  return (
    <div className="relative w-full max-w-6xl mx-auto">
      {/* Control Panel */}
      <div className="sticky top-4 z-30 mb-8">
        <TechCard variant="cyan" className="bg-black/90 backdrop-blur-sm border-cyan-400/50">
          <div className="p-4">
            <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
              {/* Progress Indicator */}
              <div className="flex items-center space-x-4">
                <span className="font-code text-cyan-400 text-sm">
                  CLASSIFIED INTEL: {currentEventIndex + 1} / {events.length}
                </span>
                <div className="w-32 sm:w-48 bg-gray-700 rounded-full h-2">
                  <div 
                    className="bg-gradient-to-r from-cyan-400 to-purple-400 h-2 rounded-full transition-all duration-500"
                    style={{ width: `${((currentEventIndex + 1) / events.length) * 100}%` }}
                  ></div>
                </div>
              </div>

              {/* Control Buttons */}
              <div className="flex items-center space-x-2 timeline-controls">
                {!hasStarted ? (
                  <TechButton variant="accent" size="sm" onClick={startProgression}>
                    <span className="mr-2">â–¶</span>
                    START INTEL BRIEFING
                  </TechButton>
                ) : (
                  <>
                    <TechButton 
                      variant="secondary" 
                      size="sm" 
                      onClick={goToPreviousEvent}
                      disabled={currentEventIndex <= 0}
                    >
                      <span>â†</span>
                    </TechButton>

                    <TechButton 
                      variant="primary" 
                      size="sm" 
                      onClick={isPlaying ? pauseProgression : startProgression}
                    >
                      <span className="mr-2">{isPlaying ? 'â¸' : 'â–¶'}</span>
                      {isPlaying ? 'PAUSE' : 'PLAY'}
                    </TechButton>

                    <TechButton 
                      variant="secondary" 
                      size="sm" 
                      onClick={goToNextEvent}
                      disabled={currentEventIndex >= events.length - 1}
                    >
                      <span>â†’</span>
                    </TechButton>

                    <TechButton 
                      variant="secondary" 
                      size="sm" 
                      onClick={resetTimeline}
                    >
                      <span className="mr-2">â†»</span>
                      RESET
                    </TechButton>
                  </>
                )}
              </div>
            </div>
          </div>
        </TechCard>
      </div>

      {/* Timeline Container */}
      <div ref={timelineRef} className="relative min-h-screen">
        {/* Central Timeline Line - Responsive */}
        <div className="absolute left-4 sm:left-1/2 sm:transform sm:-translate-x-0.5 w-1 bg-gradient-to-b from-cyan-400 via-purple-400 to-cyan-400 opacity-50" 
             style={{ height: 'calc(100% - 8rem)' }}>
        </div>

        {/* Timeline Events */}
        <div className="space-y-8 sm:space-y-16 timeline-mobile-cards sm:pl-0">
          {events.map((event, index) => {
            const isVisible = visibleEvents.includes(index);
            const isActive = currentEventIndex === index;
            const isMobile = typeof window !== 'undefined' && window.innerWidth < 640;

            return (
              <div 
                key={event.year}
                ref={el => eventRefs.current[index] = el}
                className={`relative transition-all duration-1000 ${
                  isVisible 
                    ? 'opacity-100 transform translate-y-0 timeline-reveal' 
                    : 'opacity-0 transform translate-y-8'
                } ${
                  isMobile 
                    ? 'flex flex-col' 
                    : `flex items-center ${index % 2 === 0 ? 'flex-row' : 'flex-row-reverse'}`
                }`}
              >
                {/* Event Card - Mobile: Full width, Desktop: Alternating sides */}
                <div className={`${
                  isMobile 
                    ? 'w-full' 
                    : `w-5/12 ${index % 2 === 0 ? 'pr-8' : 'pl-8'}`
                }`}>
                  <TechCard 
                    variant={index % 2 === 0 ? "cyan" : "purple"} 
                    className={`relative transition-all duration-500 ${
                      isActive ? 'ring-2 ring-cyan-400 shadow-lg shadow-cyan-400/20 scale-105' : ''
                    }`}
                  >
                    <div className="p-4 sm:p-6">
                      {/* Classification Badge */}
                      <div className="absolute top-2 right-2 sm:top-4 sm:right-4">
                        <span className={`px-2 py-1 text-xs font-code rounded ${
                          event.classification === 'DECLASSIFIED' ? 'bg-green-900 text-green-400' :
                          event.classification === 'RESTRICTED' ? 'bg-yellow-900 text-yellow-400' :
                          'bg-red-900 text-red-400'
                        } ${isActive ? 'animate-pulse' : ''}`}>
                          {event.classification}
                        </span>
                      </div>

                      {/* Event Header */}
                      <div className="mb-4">
                        <span className={`text-2xl sm:text-3xl font-titulo ${
                          index % 2 === 0 ? 'text-cyan-400' : 'text-purple-400'
                        } ${isActive ? 'animate-pulse' : ''}`}>
                          {event.year}
                        </span>
                        <h3 className="font-tech text-base sm:text-lg text-white mt-2 leading-tight">
                          {event.title}
                        </h3>
                      </div>

                      {/* Event Description */}
                      <p className="text-gray-300 text-sm sm:text-base mb-4 leading-relaxed">
                        {event.description}
                      </p>

                      {/* Impact Section */}
                      <div className={`border-l-4 pl-4 ${
                        index % 2 === 0 ? 'border-cyan-400' : 'border-purple-400'
                      }`}>
                        <p className={`text-xs sm:text-sm font-code ${
                          index % 2 === 0 ? 'text-cyan-400' : 'text-purple-400'
                        }`}>
                          IMPACT: {event.impact}
                        </p>
                      </div>

                      {/* Active Event Indicator */}
                      {isActive && (
                        <div className="absolute -top-1 -left-1 -right-1 -bottom-1 border-2 border-cyan-400 rounded-lg animate-pulse pointer-events-none"></div>
                      )}
                    </div>
                  </TechCard>
                </div>

                {/* Central Timeline Node - Hidden on mobile */}
                {!isMobile && (
                  <div className={`w-8 h-8 rounded-full border-4 bg-gray-900 z-10 flex items-center justify-center transition-all duration-500 ${
                    index % 2 === 0 ? 'border-cyan-400' : 'border-purple-400'
                  } ${isActive ? 'scale-125 shadow-lg' : ''} ${
                    isVisible ? 'animate-pulse' : ''
                  }`}>
                    <div className={`w-3 h-3 rounded-full transition-all duration-500 ${
                      index % 2 === 0 ? 'bg-cyan-400' : 'bg-purple-400'
                    } ${isActive ? 'scale-150' : ''}`}></div>
                  </div>
                )}

                {/* Spacer for desktop layout */}
                {!isMobile && <div className="w-5/12"></div>}

                {/* Mobile Timeline Node */}
                {isMobile && (
                  <div className={`absolute -left-8 top-8 w-6 h-6 rounded-full border-2 bg-gray-900 z-10 flex items-center justify-center transition-all duration-500 ${
                    index % 2 === 0 ? 'border-cyan-400' : 'border-purple-400'
                  } ${isActive ? 'scale-125 shadow-lg' : ''} ${
                    isVisible ? 'animate-pulse' : ''
                  }`}>
                    <div className={`w-2 h-2 rounded-full transition-all duration-500 ${
                      index % 2 === 0 ? 'bg-cyan-400' : 'bg-purple-400'
                    } ${isActive ? 'scale-150' : ''}`}></div>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* Timeline End Decoration */}
        {isComplete && (
          <div className="mt-16 sm:mt-32 text-center timeline-fade-in">
            <div className="inline-block bg-gray-800 px-4 sm:px-6 py-2 sm:py-3 rounded border border-cyan-400/50 mb-8">
              <span className="font-code text-cyan-400 text-xs sm:text-sm">
                END OF TIMELINE â€¢ CLASSIFICATION LEVEL: EYES ONLY
              </span>
            </div>

            {onComplete && (
              <div className="animate-bounce">
                <TechButton variant="accent" size="lg" onClick={onComplete}>
                  <span className="mr-2">ðŸš€</span>
                  PROCEED TO CHAPTER 2
                </TechButton>
              </div>
            )}
          </div>
        )}
      </div>


    </div>
  );
}

client/src/components/cypherpunk/Decentr.content:
import { useState } from "react";
import { TechCard } from "@/components/ui/TechCard";
import { TechButton } from "@/components/ui/TechButton";

interface DecentralizationVisualizationProps {
  onComplete: () => void;
}

interface NetworkNode {
  id: number;
  x: number;
  y: number;
  isActive: boolean;
  isCentral?: boolean;
}

export function DecentralizationVisualization({ onComplete }: DecentralizationVisualizationProps) {
  const [centralizedNodes, setCentralizedNodes] = useState<NetworkNode[]>([
    { id: 0, x: 50, y: 50, isActive: true, isCentral: true },
    { id: 1, x: 20, y: 20, isActive: true },
    { id: 2, x: 80, y: 20, isActive: true },
    { id: 3, x: 20, y: 80, isActive: true },
    { id: 4, x: 80, y: 80, isActive: true },
    { id: 5, x: 50, y: 15, isActive: true },
    { id: 6, x: 50, y: 85, isActive: true },
  ]);

  const [decentralizedNodes, setDecentralizedNodes] = useState<NetworkNode[]>([
    { id: 0, x: 25, y: 25, isActive: true },
    { id: 1, x: 75, y: 25, isActive: true },
    { id: 2, x: 25, y: 75, isActive: true },
    { id: 3, x: 75, y: 75, isActive: true },
    { id: 4, x: 50, y: 15, isActive: true },
    { id: 5, x: 15, y: 50, isActive: true },
    { id: 6, x: 85, y: 50, isActive: true },
    { id: 7, x: 50, y: 85, isActive: true },
  ]);

  const [centralizedFailed, setCentralizedFailed] = useState(false);
  const [decentralizedFailedNode, setDecentralizedFailedNode] = useState<number | null>(null);
  const [showExplanation, setShowExplanation] = useState(false);

  const handleCentralizedClick = (nodeId: number) => {
    if (nodeId === 0) { // Central server
      setCentralizedNodes(nodes => nodes.map(node => ({ ...node, isActive: false })));
      setCentralizedFailed(true);
      setShowExplanation(true);
    }
  };

  const handleDecentralizedClick = (nodeId: number) => {
    setDecentralizedNodes(nodes => nodes.map(node => 
      node.id === nodeId ? { ...node, isActive: false } : node
    ));
    setDecentralizedFailedNode(nodeId);
    if (!showExplanation) setShowExplanation(true);
  };

  const resetNetworks = () => {
    setCentralizedNodes(nodes => nodes.map(node => ({ ...node, isActive: true })));
    setDecentralizedNodes(nodes => nodes.map(node => ({ ...node, isActive: true })));
    setCentralizedFailed(false);
    setDecentralizedFailedNode(null);
    setShowExplanation(false);
  };

  const handleContinue = () => {
    onComplete();
  };

  return (
    <div className="space-y-8">
      {/* Explanation */}
      <TechCard variant="cyan">
        <div className="p-6">
          <h2 className="font-space-gothic text-2xl text-tech-cyan-400 mb-4">Network Architecture Comparison</h2>
          <p className="text-gray-300 mb-4">
            Explore the fundamental difference between centralized and decentralized networks. 
            Click on nodes to see how each architecture responds to failures.
          </p>
          <div className="bg-tech-cyan-900/20 border-l-4 border-tech-cyan-400 p-4 rounded">
            <p className="font-code text-tech-cyan-400 text-sm">
              INTERACTIVE DEMO: Click nodes to simulate attacks or failures
            </p>
          </div>
        </div>
      </TechCard>

      {/* Network Visualizations */}
      <div className="grid lg:grid-cols-2 gap-8">
        {/* Centralized Network */}
        <TechCard variant="purple">
          <div className="p-6">
            <h3 className="font-tech text-tech-purple-400 text-lg mb-4 text-center">
              CENTRALIZED NETWORK
            </h3>

            <div className="relative h-64 bg-gray-900 rounded-lg border border-tech-purple-600/30 overflow-hidden">
              <svg className="w-full h-full">
                {/* Connection lines */}
                {centralizedNodes.filter(node => !node.isCentral).map(node => (
                  <line
                    key={`line-${node.id}`}
                    x1={`${centralizedNodes[0].x}%`}
                    y1={`${centralizedNodes[0].y}%`}
                    x2={`${node.x}%`}
                    y2={`${node.y}%`}
                    stroke={node.isActive && centralizedNodes[0].isActive ? '#8b5cf6' : '#ef4444'}
                    strokeWidth="2"
                    className="transition-all duration-500"
                    opacity={node.isActive && centralizedNodes[0].isActive ? 0.8 : 0.3}
                  />
                ))}

                {/* Nodes */}
                {centralizedNodes.map(node => (
                  <circle
                    key={node.id}
                    cx={`${node.x}%`}
                    cy={`${node.y}%`}
                    r={node.isCentral ? "16" : "8"}
                    fill={node.isActive ? (node.isCentral ? '#8b5cf6' : '#a855f7') : '#ef4444'}
                    className="cursor-pointer transition-all duration-500 hover:r-12"
                    onClick={() => handleCentralizedClick(node.id)}
                  />
                ))}

                {/* Labels */}
                {centralizedNodes.map(node => (
                  <text
                    key={`label-${node.id}`}
                    x={`${node.x}%`}
                    y={`${node.y + (node.isCentral ? 8 : 5)}%`}
                    textAnchor="middle"
                    fill="white"
                    fontSize="10"
                    className="font-code"
                  >
                    {node.isCentral ? 'SERVER' : `U${node.id}`}
                  </text>
                ))}
              </svg>

              {centralizedFailed && (
                <div className="absolute inset-0 flex items-center justify-center bg-red-900/50">
                  <span className="text-red-400 font-code text-lg">NETWORK FAILURE</span>
                </div>
              )}
            </div>

            <div className="mt-4 text-center">
              <p className="text-gray-400 text-sm mb-2">Click the central server to simulate failure</p>
              <div className="flex items-center justify-center text-xs">
                <span className="w-2 h-2 bg-tech-purple-400 rounded-full mr-2"></span>
                <span className="text-gray-500">Single point of failure</span>
              </div>
            </div>
          </div>
        </TechCard>

        {/* Decentralized Network */}
        <TechCard variant="cyan">
          <div className="p-6">
            <h3 className="font-tech text-tech-cyan-400 text-lg mb-4 text-center">
              DECENTRALIZED NETWORK
            </h3>

            <div className="relative h-64 bg-gray-900 rounded-lg border border-tech-cyan-600/30 overflow-hidden">
              <svg className="w-full h-full">
                {/* Connection lines - mesh topology */}
                {decentralizedNodes.map(node1 => 
                  decentralizedNodes.filter(node2 => {
                    const distance = Math.sqrt(
                      Math.pow(node1.x - node2.x, 2) + Math.pow(node1.y - node2.y, 2)
                    );
                    return distance < 40 && node1.id !== node2.id;
                  }).map(node2 => (
                    <line
                      key={`line-${node1.id}-${node2.id}`}
                      x1={`${node1.x}%`}
                      y1={`${node1.y}%`}
                      x2={`${node2.x}%`}
                      y2={`${node2.y}%`}
                      stroke={node1.isActive && node2.isActive ? '#06b6d4' : '#64748b'}
                      strokeWidth="2"
                      className="transition-all duration-500"
                      opacity={node1.isActive && node2.isActive ? 0.6 : 0.2}
                    />
                  ))
                )}

                {/* Nodes */}
                {decentralizedNodes.map(node => (
                  <circle
                    key={node.id}
                    cx={`${node.x}%`}
                    cy={`${node.y}%`}
                    r="10"
                    fill={node.isActive ? '#06b6d4' : '#ef4444'}
                    className="cursor-pointer transition-all duration-500 hover:r-12"
