                    </div>
                  </div>
                </div>
                {/* Enhanced Speech bubble tail */}
                <div className="absolute bottom-0 right-8 transform translate-y-full">
                  <div className={`w-0 h-0 border-x-8 border-t-8 border-solid border-transparent ${getBubbleColor().split(' ')[1]}`}></div>
