import { useRef, useEffect, useState } from 'react';
// Mobile-first code editor fallback
import { TechButton } from '@/components/ui/TechButton';
import { cn } from '@/lib/utils';

interface CodeEditorMobileProps {
  defaultValue?: string;
  language?: string;
  onRun?: (code: string) => void;
  readOnly?: boolean;
  theme?: 'vs-dark' | 'light';
  className?: string;
  placeholder?: string;
}

export function CodeEditorMobile({
  defaultValue = '',
  language = 'rust',
  onRun,
  readOnly = false,
  theme = 'vs-dark',
  className,
  placeholder = 'Start coding...'
}: CodeEditorMobileProps) {
  const editorRef = useRef<any>(null);
  const [code, setCode] = useState(defaultValue);
  const [isRunning, setIsRunning] = useState(false);
  const [editorHeight, setEditorHeight] = useState(300);

  // Dynamically adjust editor height for mobile
  useEffect(() => {
    const updateHeight = () => {
      const viewportHeight = window.innerHeight;
      const availableHeight = viewportHeight - 200; // Account for header, buttons, etc.
      const mobileHeight = Math.min(Math.max(availableHeight * 0.4, 250), 400);
      const desktopHeight = Math.min(Math.max(availableHeight * 0.6, 300), 600);

      setEditorHeight(window.innerWidth < 768 ? mobileHeight : desktopHeight);
    };

    updateHeight();
    window.addEventListener('resize', updateHeight);
    return () => window.removeEventListener('resize', updateHeight);
  }, []);

  // Mobile-optimized viewport handling
  useEffect(() => {
    if ('visualViewport' in window) {
      const handleViewportChange = () => {
        if (window.visualViewport) {
          const newHeight = window.visualViewport.height * 0.4;
          if (newHeight > 150) {
            setEditorHeight(newHeight);
          }
        }
      };

      window.visualViewport?.addEventListener('resize', handleViewportChange);
      return () => window.visualViewport?.removeEventListener('resize', handleViewportChange);
    }
  }, []);

  function handleChange(value: string | undefined) {
    setCode(value || '');
  }

  async function handleRun() {
    if (!onRun || isRunning) return;

    setIsRunning(true);
    try {
      await onRun(code);
    } finally {
      setIsRunning(false);
    }
  }

  return (
    <div className={cn("space-y-4", className)}>
      {/* Editor container with mobile-first styling */}
      <div className="relative">
        <div 
          className="border-2 border-tech-cyan-400/30 rounded-lg overflow-hidden bg-gray-900"
          style={{ height: `${editorHeight}px` }}
        >
          <textarea
            className="w-full h-full bg-gray-900 text-green-400 font-mono p-4 resize-none outline-none"
            value={code}
            onChange={(e) => handleChange(e.target.value)}
            readOnly={readOnly}
            placeholder={placeholder}
            style={{
              fontSize: window.innerWidth < 768 ? '14px' : '16px',
              lineHeight: window.innerWidth < 768 ? '20px' : '24px',
              fontFamily: 'ui-monospace, SFMono-Regular, "SF Mono", Consolas, "Liberation Mono", Menlo, monospace',
            }}
          />
        </div>

        {/* Mobile optimization indicator */}
        <div className="absolute top-2 right-2 opacity-50">
          <div className="flex items-center space-x-1 text-xs text-gray-400">
            <span className="block sm:hidden">ðŸ“±</span>
            <span className="hidden sm:block md:hidden">ðŸ“±</span>
            <span className="hidden md:block">ðŸ’»</span>
          </div>
        </div>
      </div>

      {/* Mobile-optimized run button */}
      {onRun && (
        <div className="flex flex-col sm:flex-row gap-3 sm:justify-between sm:items-center">
          <TechButton
            onClick={handleRun}
            disabled={isRunning}
            variant="primary"
            size="lg"
            className="w-full sm:w-auto order-2 sm:order-1"
          >
            {isRunning ? (
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                <span>Running...</span>
              </div>
            ) : (
              <div className="flex items-center space-x-2">
                <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
                </svg>
                <span>Run Code</span>
              </div>
            )}
          </TechButton>

          {/* Mobile-friendly code stats */}
          <div className="text-mobile-sm text-gray-400 font-mono order-1 sm:order-2 text-center sm:text-right">
            <div className="flex justify-center sm:justify-end space-x-4">
              <span>Lines: {code.split('\n').length}</span>
              <span>Chars: {code.length}</span>
            </div>
          </div>
        </div>
      )}

      {/* Mobile typing assistance */}
      <div className="block sm:hidden">
        <div className="text-xs text-gray-500 text-center">
          Tip: Tap and hold for selection, double-tap for word selection
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/DecryptedText.t.content:
import { motion, HTMLMotionProps } from 'framer-motion'
import { useState, useEffect, useRef } from 'react'

const styles = {
    wrapper: {
        display: 'inline-block',
        whiteSpace: 'pre-wrap',
    },
    srOnly: {
        position: 'absolute' as 'absolute',
        width: '1px',
        height: '1px',
        padding: 0,
        margin: '-1px',
        overflow: 'hidden',
        clip: 'rect(0,0,0,0)',
        border: 0,
    },
}

interface DecryptedTextProps extends HTMLMotionProps<'span'> {
    text: string
    speed?: number
    maxIterations?: number
    sequential?: boolean
    revealDirection?: 'start' | 'end' | 'center'
    useOriginalCharsOnly?: boolean
    characters?: string
    className?: string
    parentClassName?: string
    encryptedClassName?: string
    animateOn?: 'view' | 'hover'
}

export default function DecryptedText({
    text,
    speed = 50,
    maxIterations = 10,
    sequential = false,
    revealDirection = 'start',
    useOriginalCharsOnly = false,
    characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz!@#$%^&*()_+',
    className = '',
    parentClassName = '',
    encryptedClassName = '',
    animateOn = 'hover',
    ...props
}: DecryptedTextProps) {
    const [displayText, setDisplayText] = useState<string>(text)
    const [isHovering, setIsHovering] = useState<boolean>(false)
    const [isScrambling, setIsScrambling] = useState<boolean>(false)
    const [revealedIndices, setRevealedIndices] = useState<Set<number>>(new Set())
    const [hasAnimated, setHasAnimated] = useState<boolean>(false)
    const containerRef = useRef<HTMLSpanElement>(null)

    useEffect(() => {
        let interval: NodeJS.Timeout;
        let currentIteration = 0

        const getNextIndex = (revealedSet: Set<number>): number => {
            const textLength = text.length
            switch (revealDirection) {
                case 'start':
                    return revealedSet.size
                case 'end':
                    return textLength - 1 - revealedSet.size
                case 'center': {
                    const middle = Math.floor(textLength / 2)
                    const offset = Math.floor(revealedSet.size / 2)
                    const nextIndex =
                        revealedSet.size % 2 === 0
                            ? middle + offset
                            : middle - offset - 1

                    if (nextIndex >= 0 && nextIndex < textLength && !revealedSet.has(nextIndex)) {
                        return nextIndex
                    }

                    for (let i = 0; i < textLength; i++) {
                        if (!revealedSet.has(i)) return i
                    }
                    return 0
                }
                default:
                    return revealedSet.size
            }
        }

        const availableChars = useOriginalCharsOnly
            ? Array.from(new Set(text.split(''))).filter((char) => char !== ' ')
            : characters.split('')

        const shuffleText = (originalText: string, currentRevealed: Set<number>): string => {
            if (useOriginalCharsOnly) {
                const positions = originalText.split('').map((char, i) => ({
                    char,
                    isSpace: char === ' ',
                    index: i,
                    isRevealed: currentRevealed.has(i),
                }))

                const nonSpaceChars = positions
                    .filter((p) => !p.isSpace && !p.isRevealed)
                    .map((p) => p.char)

                for (let i = nonSpaceChars.length - 1; i > 0; i--) {
                    const j = Math.floor(Math.random() * (i + 1))
                    ;[nonSpaceChars[i], nonSpaceChars[j]] = [nonSpaceChars[j], nonSpaceChars[i]]
                }

                let charIndex = 0
                return positions
                    .map((p) => {
                        if (p.isSpace) return ' '
                        if (p.isRevealed) return originalText[p.index]
                        return nonSpaceChars[charIndex++]
                    })
                    .join('')
            } else {
                return originalText
                    .split('')
                    .map((char, i) => {
                        if (char === ' ') return ' '
                        if (currentRevealed.has(i)) return originalText[i]
                        return availableChars[Math.floor(Math.random() * availableChars.length)]
                    })
                    .join('')
            }
        }

        if (isHovering) {
            setIsScrambling(true)
            interval = setInterval(() => {
                setRevealedIndices((prevRevealed) => {
                    if (sequential) {
                        if (prevRevealed.size < text.length) {
                            const nextIndex = getNextIndex(prevRevealed)
                            const newRevealed = new Set(prevRevealed)
                            newRevealed.add(nextIndex)
                            setDisplayText(shuffleText(text, newRevealed))
                            return newRevealed
                        } else {
                            clearInterval(interval)
                            setIsScrambling(false)
                            return prevRevealed
                        }
                    } else {
                        setDisplayText(shuffleText(text, prevRevealed))
                        currentIteration++
                        if (currentIteration >= maxIterations) {
                            clearInterval(interval)
                            setIsScrambling(false)
                            setDisplayText(text)
                        }
                        return prevRevealed
                    }
                })
            }, speed)
        } else {
            setDisplayText(text)
            setRevealedIndices(new Set())
            setIsScrambling(false)
        }

        return () => {
            if (interval) clearInterval(interval)
        }
    }, [
        isHovering,
        text,
        speed,
        maxIterations,
        sequential,
        revealDirection,
        characters,
        useOriginalCharsOnly,
    ])

    useEffect(() => {
        if (animateOn !== 'view') return

        const observerCallback = (entries: IntersectionObserverEntry[]) => {
            entries.forEach((entry) => {
                if (entry.isIntersecting && !hasAnimated) {
                    setIsHovering(true)
                    setHasAnimated(true)
                }
            })
        }

        const observerOptions = {
            root: null,
            rootMargin: '0px',
            threshold: 0.1,
        }

        const observer = new IntersectionObserver(observerCallback, observerOptions)
        const currentRef = containerRef.current
        if (currentRef) {
            observer.observe(currentRef)
        }

        return () => {
            if (currentRef) {
                observer.unobserve(currentRef)
            }
        }
    }, [animateOn, hasAnimated])

    const hoverProps =
        animateOn === 'hover'
            ? {
                onMouseEnter: () => setIsHovering(true),
                onMouseLeave: () => setIsHovering(false),
            }
            : {}

    return (
        <motion.span className={parentClassName} ref={containerRef} style={styles.wrapper} {...hoverProps} {...props}>
            <span style={styles.srOnly}>{displayText}</span>

            <span aria-hidden="true">
                {displayText.split('').map((char, index) => {
                    const isRevealedOrDone =
                        revealedIndices.has(index) || !isScrambling || !isHovering

                    return (
                        <span
                            key={index}
                            className={isRevealedOrDone ? className : encryptedClassName}
                        >
                            {char}
                        </span>
                    )
                })}
            </span>
        </motion.span>
    )
}

